import { useEffect, useState } from "react";
import { Form, redirect, useNavigate, useParams } from "react-router-dom";

async function updateTable(table_id, updatedTable) {
  try {
    let response = await fetch(`/table/${table_id}`, {
      method: "PUT",
      body: JSON.stringify(updatedTable),
      headers: new Headers({
        "Content-Type": "application/json",
      }),
    }).then((res) => {
      if (!res.ok) {
        throw Error(`Could not update Table ${table_id}`);
      }
      return res.json();
    });
    return response;
  } catch (error) {
    console.error("Error:", error);
  }
}

export async function updateAction({ request, params }) {
  const formData = await request.formData();
  let table = Object.fromEntries(formData);
  if (!table) {
    throw new Error("Error in updating Book " + params.id);
  }
  const { table_id } = params;
  const updatedtle = { table_id, ...table };
  await updateTable(table_id, updatedtle);
  console.log(updateTable(table_id, updatedtle));
  return redirect(`/admin/`);
}

export default function EditTable() {
  const { table_id } = useParams();
  const navigate = useNavigate();
  console.log(table_id);

  // Load initial product data from API
  const [table, setTeble] = useState();
  useEffect(() => {
    async function fetchTable() {
      const response = await fetch(`/table/${table_id}`);
      const data = await response.json();
      setTeble(data);
    }
    fetchTable();
  }, [table_id]);
  // Render loading state while waiting for product data to load
  if (!table) {
    return <div>Loading...</div>;
  }

  return (
    <Form replace method="put">
      <label>
        Table ID
        <input type="text" defaultValue={table.table_id} name="table_id" />
      </label>
      <label>
        Table number
        <input
          type="text"
          defaultValue={table.table_number}
          name="table_number"
        ></input>
      </label>
      <label>
        Capacity
        <input defaultValue={table.capacity} name="capacity"></input>
      </label>

      <label>
        Status
        <select name="available" defaultValue={table.available}>
          <option value="true">Available</option>
          <option value="false">Booked</option>
        </select>
      </label>
      <label>
        Zone
        <input type="text" defaultValue={table.zone} name="zone"></input>
      </label>

      <div className="bt-submit">
        <button className="bt-s" type="submit">
          บันทึก
        </button>
        <button
          className="bt-cancel"
          type="button"
          onClick={() => {
            navigate(-1);
          }}
        >
          ยกเลิก
        </button>
      </div>
    </Form>
  );
}
