import { Link, useLoaderData } from "react-router-dom";
import { useState } from "react";
import _ from "lodash";

export const tableLoader = async () => {
  const res = await fetch("/table");
  if (!res.ok) {
    throw new Error("Could not fetch the Tables");
  }
  return res.json();
};

function status(props) {
  if (props === true) {
    return <div className="info">ยังไม่ได้จอง</div>;
  } else {
    return <div className="danger">จองแล้ว</div>;
  }
}

const TableListPage = () => {
  const tables = useLoaderData();
  const [filteredTables, setFilteredTables] = useState(tables);

  const filterTablesByZone = (zone) => {
    const filteredTables = tables.filter((table) => table.zone === zone);
    return filteredTables;
  };

  const handleAddToreservation = (e) => {
    let reservation = [];
    if (localStorage.getItem("reservation")) {
      reservation = JSON.parse(localStorage.getItem("reservation"));
    }
    reservation.push({
      ...e,
      count: 1,
    });
    let unique = _.unionWith(reservation, _.isEqual);
    localStorage.setItem("reservation", JSON.stringify(unique));
  };

  return (
    <>
      <h1>รายการโต๊ะ</h1>
      <div>
        <button onClick={() => setFilteredTables(tables)}>ทั้งหมด</button>{" "}
        <button onClick={() => setFilteredTables(filterTablesByZone("A"))}>
          โซน A
        </button>{" "}
        <button onClick={() => setFilteredTables(filterTablesByZone("B"))}>
          โซน B
        </button>{" "}
        <button onClick={() => setFilteredTables(filterTablesByZone("C"))}>
          โซน C
        </button>{" "}
        <button onClick={() => setFilteredTables(filterTablesByZone("D"))}>
          โซน D
        </button>
      </div>

      <div>
        {filteredTables.map((table) => (
          <div key={table.table_id}>
            <div className="img-table">
              <img
                src={require(`../img/${table.table_number}.jpg`)}
                alt=""
                style={{ width: "320px", height: "300px", objectFit: "cover" }}
              />
            </div>
            <Link
              to={table.table_id}
              onClick={() => handleAddToreservation(table)}
            >
              <h2>โต๊ะเลขที่: {table.table_number}</h2>
            </Link>
            <p>จำนวนที่นั่ง: {table.capacity}</p>
            <div>{status(table.available)}</div>
          </div>
        ))}
      </div>
    </>
  );
};

export default TableListPage;
