import { useState } from "react";

const TableLayouts = () => {
  const [selectedTable, setSelectedTable] = useState(null);

  const handleTableClick = (tableId) => {
    setSelectedTable(tableId);
  };

  const tableLayout = [
    { id: 1, capacity: 4, reserved: false },
    { id: 2, capacity: 2, reserved: true },
    { id: 3, capacity: 4, reserved: false },
    { id: 4, capacity: 6, reserved: false },
    // Add more tables here...
  ];

  return (
    <div className="table-layout">
      {tableLayout.map((table) => (
        <div
          key={table.id}
          className={`table ${table.reserved ? "reserved" : ""} ${
            selectedTable === table.id ? "selected" : ""
          }`}
          onClick={() => handleTableClick(table.id)}
        >
          {table.capacity}
        </div>
      ))}
    </div>
  );
};

export default TableLayouts;
