import { Link, redirect } from "react-router-dom";
import { useState } from "react";
const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const handleSubmit = async (e) => {
    e.preventDefault();
    // // can use formData also
    // const formData = e.formData();
    // let loginData = Object.fromEntries(formData);
    try {
      const response = await fetch("/auth", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password }), // type match "Content-Type" header
      });
      if (response.ok) {
        const data = await response.json();
        setEmail("");
        setPassword("");
        console.log(data);
        return redirect("/");
      } else {
        console.log("login error");
        throw new Error("Login Error");
      }
    } catch (error) {
      throw new Error("Error. Try again later!");
    }
  };

  return (
    <>
      <div>
        <form onSubmit={handleSubmit} className="login-form">
          <h1>Login</h1>
          <div className="form-input">
            <label htmlFor="email">Email</label>
            <input
              type="text"
              name="email"
              id="email"
              value={email}
              placeholder="youremail@site.com"
              autoComplete="off"
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="form-input">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              name="password"
              id="password"
              value={password}
              placeholder="password"
              autoComplete="off"
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit">Login</button>
        </form>
      </div>
      <div>
        or <Link to="/signup">Signup</Link> if you need an accout.
      </div>
    </>
  );
};
export default LoginPage;
