import { useEffect, useState } from "react";
import { Form, redirect, useNavigate, useParams } from "react-router-dom";

async function updateTable(id, updatedTable) {
  try {
    let response = await fetch(`/table/${id}`, {
      method: "PUT",
      body: JSON.stringify(updatedTable),
      headers: new Headers({
        "Content-Type": "application/json",
      }),
    }).then((res) => {
      if (!res.ok) {
        throw Error({ error: `Could not update Table ${id}` });
      }
      return res.json();
    });
    return response;
  } catch (error) {
    console.error("Error:", error);
  }
}

export async function updateAction({ request, params }) {
  const formData = await request.formData();
  let table = Object.fromEntries(formData);
  if (!table) {
    throw new Error("Error in updating table " + params.id);
  }
  const { id } = params;
  const updatedtable = { id, ...table };
  await updateTable(id, updatedtable);
  return redirect(`/admin/`);
}

export default function EditTable() {
  const { id } = useParams();
  const navigate = useNavigate();

  // Load initial product data from API
  const [table, setTeble] = useState();
  useEffect(() => {
    async function fetchTable() {
      const response = await fetch(`/table/${id}`);
      const data = await response.json();
      setTeble(data);
    }
    fetchTable();
  }, [id]);
  // Render loading state while waiting for product data to load
  if (!table) {
    return <div>Loading...</div>;
  }

  return (
    <Form replace method="put">
      <label>
        ID:
        <input type="text" defaultValue={table.table_id} name="id" />
      </label>
      <label>
        ชื่อหนังสือ:
        <input type="text" name="title" defaultValue={table.teble_number} />
      </label>

      <div className="bt-submit">
        <button className="bt-s" type="submit">
          บันทึก
        </button>
        <button
          className="bt-cancel"
          type="button"
          onClick={() => {
            navigate(-1);
          }}
        >
          ยกเลิก
        </button>
      </div>
    </Form>
  );
}
