import React, { useState } from 'react';
import './Table.css';
export const loadReserve = async () => {
  const res = await fetch("/reserve");
  if (!res.ok) {
    throw Error("Could not fetch the reserve");
  }

  return res.json();
};
function status(props) {
  if (props === true) {
    return <div className="info">Available</div>;
  } else {
    return <div className="danger">Booked</div>;
  }
}


function Table() {
  const [data, setData] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  // Function to handle adding a new row to the table
  const handleAddRow = () => {
    const newRow = {
      column1: 'Value 1',
      column2: 'Value 2',
      column3: 'Value 3',
      column4: 'Value 4',
      column5: 'Value 5',
      column6: 'Value 6',
    };
    setData((prevData) => [...prevData, newRow]);
  };

  // Function to handle editing a row in the table
  const handleEditRow = (rowId) => {
    // Implement your logic for editing a row here
  };

  // Function to handle searching/filtering the table
  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
    // Implement your logic for filtering the table based on the search term
  };

  return (
    <div className="table-container">
      <div className="top-bar">
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>
        <button className="add-button" onClick={handleAddRow}>Add</button>
      </div>
      <table className="table">
        <thead>
          <tr>
            <th>Table No.</th>
            <th>Zone</th>
            <th>Name</th>
            <th>Date Booking</th>
            <th>Time Booking</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {/* Render table rows dynamically */}
          {data.map((row, index) => (
            <tr key={index}>
              <td>{row.column1}</td>
              <td>{row.column2}</td>
              <td>{row.column3}</td>
              <td>{row.column4}</td>
              <td>{row.column5}</td>
              <td>{row.column6}</td>
              <td>
                <button onClick={() => handleEditRow(index)}>Edit</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Table;
