import { useState } from "react";
import { useLoaderData } from "react-router-dom";
export const tablesLoader = async () => {
  const res = await fetch("/table");
  if (!res.ok) {
    throw new Error("Could not fetch the Tables");
  }
  return res.json();
};

const RestaurantLayout = () => {
  const tables = useLoaderData();
  const [selectedTable, setSelectedTable] = useState(null);

  const handleTableClick = (tableId) => {
    setSelectedTable(tableId);
  };

  const tableLayout = [
    { id: 1, zone: "A", capacity: 4, reserved: true },
    { id: 2, zone: "A", capacity: 2, reserved: true },
    { id: 3, zone: "B", capacity: 4, reserved: false },
    { id: 4, zone: "B", capacity: 6, reserved: false },
    // Add more tables here...
  ];

  const zones = Array.from(new Set(tables.map((table) => table.zone)));

  return (
    <div className="restaurant-layout">
      {zones.map((zone) => (
        <div key={zone} className="zone">
          <h2>Zone {zone}</h2>
          <div className="tables">
            {tableLayout
              .filter((table) => table.zone === zone)
              .map((table) => (
                <div
                  key={table.id}
                  className={`table ${table.reserved ? "reserved" : ""} ${
                    selectedTable === table.id ? "selected" : ""
                  }`}
                  onClick={() => handleTableClick(table.id)}
                >
                  {table.capacity}
                </div>
              ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default RestaurantLayout;
