import { useState } from "react";
import { Link } from "react-router-dom";

const ReservePage = () => {
  const reserve = JSON.parse(localStorage.getItem("reservation")) || [];
  const [checkedItems, setCheckedItems] = useState([]);
  const [name, setName] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [showPopup, setShowPopup] = useState(false);

  const handleCheckboxChange = (event) => {
    const isChecked = event.target.checked;
    const itemId = event.target.value;
    if (isChecked) {
      setCheckedItems((prevState) => [...prevState, itemId]);
    } else {
      setCheckedItems((prevState) => prevState.filter((id) => id !== itemId));
    }
  };

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleDateChange = (event) => {
    setDate(event.target.value);
  };

  const handleTimeChange = (event) => {
    setTime(event.target.value);
  };

  const handleReserve = async (event) => {
    event.preventDefault();
    // create orderData object
    const reservationData = {
      customerName: name,
      table_id: "1003",
      reservationDate: date,
    };
    try {
      const response = await fetch("/your-post-url", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(reservationData),
      });

      if (!response.ok) {
        throw new Error("Could not create reservation.");
      }
      // กำหนดข้อความ pop up เมื่อสั่งซื้อสำเร็จ
      setShowPopup(true);
    } catch (error) {
      console.error(error);
    }
  };

  function handleDeleteClick(itemId) {
    const newReserve = reserve.filter((item) => item.id !== itemId);
    localStorage.setItem("reservation", JSON.stringify(newReserve));
    window.location.reload();
  }

  const handlePopupClose = () => {
    // close the popup and clear the reservation
    setShowPopup(false);
    localStorage.removeItem("reservation");
    window.location.reload();
  };

  return (
    <>
      <form onSubmit={handleReserve}>
        <div className="container-reserve">
          <div className="row">
            <div className="name-reserve">
              <label htmlFor="name">ชื่อผู้จอง:</label>
              <input
                type="text"
                id="name"
                name="name"
                value={name}
                onChange={handleNameChange}
                required
              />
            </div>
            <div className="date-reserve">
              <label htmlFor="date">วันที่:</label>
              <input
                type="date"
                id="date"
                name="date"
                value={date}
                onChange={handleDateChange}
                required
              />
            </div>
          </div>

          {reserve.length === 0 ? (
            <p className="cart-empty">ไม่มีรายการจอง</p>
          ) : (
            <div className="row">
              {reserve.map((item, index) => (
                <div className="reserve-item" key={index}>
                  <Link to={`${item.id}`}>
                    <div className="reserve-item-title">
                      <span>รหัสพัสดุ </span>
                      {item.id}
                    </div>
                  </Link>
                  <div className="reserve-item-details">
                    <div className="reserve-item-count">
                      จำนวน {item.count} ชิ้น
                    </div>
                    <div className="reserve-box">
                      <div className="reserve-item-checkbox">
                        <input
                          className="reserve-item-checkbox-input"
                          type="checkbox"
                          name="id"
                          value={item.id}
                          onChange={handleCheckboxChange}
                          checked={checkedItems.includes(item.id)}
                        />
                      </div>
                      <div className="reserve-item-delete">
                        <button onClick={() => handleDeleteClick(item.id)}>
                          ลบ
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}

          <div className="bt-reserve">
            <button type="submit" className="bt-reserve-submit">
              ยืนยันการจอง
            </button>
          </div>
          {showPopup && (
            <div className="popup">
              <div className="popup-content">
                <h2>การจองของคุณสำเร็จ</h2>
                <p>
                  ชื่อผู้จอง: {name}
                  <br />
                  วันที่: {date}
                  <br />
                  เวลา: {time}
                  <br />
                  รายการจอง:
                  <ul>
                    {reserve
                      .filter((parcel) => checkedItems.includes(parcel.id))
                      .map((parcel) => (
                        <li key={parcel.id}>
                          {"รหัสพัสดุ"}
                          {parcel.id}
                          <span> จำนวน</span>
                          <span>
                            {" "}
                            {parcel.count} {"ชิ้น"}
                          </span>
                        </li>
                      ))}
                  </ul>
                </p>
                <button onClick={handlePopupClose}>ยืนยัน</button>
              </div>
            </div>
          )}
        </div>
      </form>
    </>
  );
};

export default ReservePage;
