import React, { useState } from "react";
import "./../Table.css";
import { useLoaderData } from "react-router-dom";

function Status(props) {
  if (props) {
    return <div className="info">Available</div>;
  } else {
    return <div className="danger">Booked</div>;
  }
}

function AdminTable() {
  const myrerve = useLoaderData();
  const [availability, setAvailability] = useState(
    myrerve.map((e) => e.tableInfo.available)
  );

  const MyreserveRows = myrerve.map((e, index) => {
    const handleManageClick = () => {
      // Update the availability status to true
      const updatedAvailability = [...availability];
      updatedAvailability[index] = true;
      setAvailability(updatedAvailability);
    };

    return (
      <tr key={index}>
        <td>{e.tableInfo.table_number}</td>
        <td>{e.tableInfo.zone}</td>
        <td>{e.customerName}</td>
        <td>{e.reservationDate}</td>
        <td>{e.reservationTime}</td>
        <td>{<Status props={e.tableInfo.available} />}</td>
        <td>
          <button onClick={handleManageClick}>Manage</button>
        </td>
      </tr>
    );
  });

  return (
    <div className="tab-container">
      <table className="reserve-table">
        <thead>
          <tr>
            <th>Table No.</th>
            <th>Zone</th>
            <th>Name</th>
            <th>Date Booking</th>
            <th>Time Booking</th>
            <th>Status</th>
            <th>Manage</th>
          </tr>
        </thead>
        <tbody>
          {myrerve.length ? (
            MyreserveRows
          ) : (
            <tr>
              <td colSpan="7" className="nothavebooking">
                Not have Booking List
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default AdminTable;
