import { Form, redirect, useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
async function updateBook(table_id, updatedBook) {
  try {
    let response = await fetch(`/table/${table_id}`, {
      method: "PUT",
      body: JSON.stringify(updatedBook),
      headers: new Headers({
        "Content-Type": "application/json",
      }),
    }).then((res) => {
      if (!res.ok) {
        throw Error({ error: `Could not update book ${table_id}` });
      }
      return res.json();
    });
    return response;
  } catch (error) {
    console.error("Error:", error);
  }
}

export async function updateAction(values) {
  const { table_id } = useParams();
  const updatedBook = { table_id, ...values };
  await updateBook(table_id, updatedBook);
  return "/admin/";
}

export default function EditTable() {
  const { table_id } = useParams();
  const navigate = useNavigate();

  // Load initial product data from API
  const [book, setBook] = useState();
  useEffect(() => {
    async function fetchBook() {
      const response = await fetch(`/table/${table_id}`);
      const data = await response.json();
      setBook(data);
    }
    fetchBook();
  }, [table_id]);
  // Render loading state while waiting for product data to load
  if (!book) {
    return <div>Loading...</div>;
  }

  return (
    <Form replace method="put">
      <label>
        ID:
        <input type="text" defaultValue={book.table_id} name="table_id" />
      </label>
      <label>
        ชื่อหนังสือ:
        <input
          type="text"
          name="table_number"
          defaultValue={book.table_number}
        />
      </label>

      <label>
        จำนวนหน้า:
        <input type="number" defaultValue={book.capacity} name="capacity" />
      </label>
      <label>
        ปีที่ตีพิมพ์:
        <input type="boolean" defaultValue={book.available} name="available" />
      </label>
      <label>
        ปีที่ตีพิมพ์:
        <input type="zone" defaultValue={book.zone} name="zone" />
      </label>

      <div className="bt-submit">
        <button className="bt-s" type="submit">
          บันทึก
        </button>
        <button
          className="bt-cancel"
          type="button"
          onClick={() => {
            navigate(-1);
          }}
        >
          ยกเลิก
        </button>
      </div>
    </Form>
  );
}

