import { useState, useHistory } from "react";
import { Link } from "react-router-dom";

const ReservePage = () => {
  const reserve = JSON.parse(localStorage.getItem("reservation")) || [];
  const [name, setName] = useState("");
  const [date, setDate] = useState("");
  const history = useHistory();

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleDateChange = (event) => {
    setDate(event.target.value);
  };

  const handleReserve = async (event) => {
    event.preventDefault();
    // create reservationData object
    const reservationData = {
      customerName: name,
      table_id: reserve[0].table_id,
      reservationDate: date,
    };
    try {
      const response = await fetch("http://localhost:4000/reserve", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(reservationData),
      });

      if (!response.ok) {
        throw new Error("Could not create reservation.");
      }
      // set success message for reservation
    } catch (error) {
      console.error(error);
    }
  };

  const handleCancel = () => {
    // Clear form inputs
    setName("");
    setDate("");
    // Clear localStorage
    localStorage.removeItem("reservation");
    // Navigate back to the previous page
    history.goBack();
  };

  return (
    <>
      <form onSubmit={handleReserve}>
        <div className="container-reserve">
          <div className="row">
            <div className="name-reserve">
              <label htmlFor="name">ชื่อผู้จอง:</label>
              <input
                type="text"
                id="name"
                name="name"
                value={name}
                onChange={handleNameChange}
                required
              />
            </div>
            <div className="date-reserve">
              <label htmlFor="date">วันที่:</label>
              <input
                type="date"
                id="date"
                name="date"
                value={date}
                onChange={handleDateChange}
                required
              />
            </div>
          </div>
          {reserve.length === 0 ? (
            <p className="cart-empty">ไม่มีโต๊ะนี้</p>
          ) : (
            <div className="row">
              {reserve.map((item, index) => (
                <div className="reserve-item" key={index}>
                  <Link to={`${item.table_id}`}>
                    <div className="reserve-item-title">
                      <span>โต๊ะ: </span>
                      {item.table_id}
                    </div>
                  </Link>
                </div>
              ))}
            </div>
          )}

          <div className="bt-reserve">
            <button type="submit" className="bt-reserve-submit">
              ยืนยันการจอง
            </button>
            <button
              type="button"
              className="bt-reserve-cancel"
              onClick={handleCancel}
            >
              ยกเลิก
            </button>
          </div>
        </div>
      </form>
    </>
  );
};

export default ReservePage;
