import { useEffect, useState } from "react";
import { Form, useForm } from "react-final-form";
import { useNavigate, useParams } from "react-router-dom";

async function updateTable(table_id, updatedTable) {
  try {
    let response = await fetch(`/table/${table_id}`, {
      method: "PUT",
      body: JSON.stringify(updatedTable),
      headers: new Headers({
        "Content-Type": "application/json",
      }),
    });
    if (!response.ok) {
      throw new Error(`Could not update Table ${table_id}`);
    }
    return response.json();
  } catch (error) {
    console.error("Error:", error);
    throw error;
  }
}

export async function updateAction({ values, params }) {
  const { table_id } = params;
  const updatedTable = { table_id, ...values };
  await updateTable(table_id, updatedTable);
  return { redirect: "/admin/" };
}

export default function EditTable() {
  const { table_id } = useParams();
  const navigate = useNavigate();
  const { handleSubmit, form, submitting } = useForm();

  const [table, setTable] = useState();
  useEffect(() => {
    async function fetchTable() {
      const response = await fetch(`/table/${table_id}`);
      const data = await response.json();
      setTable(data);
    }
    fetchTable();
  }, [table_id]);

  const onSubmit = async (values) => {
    try {
      await handleSubmit(updateAction)({ values, params: { table_id } });
    } catch (error) {
      console.error("Error:", error);
    }
  };

  if (!table) {
    return <div>Loading...</div>;
  }

  return (
    <Form
      onSubmit={onSubmit}
      initialValues={{
        table_number: table.table_number,
        available: table.available.toString(),
      }}
    >
      {({ handleSubmit }) => (
        <form onSubmit={handleSubmit}>
          <label>
            Table
            <input type="text" name="table_number" />
          </label>
          <label>
            Status
            <select name="available">
              <option value="true">Available</option>
              <option value="false">Booked</option>
            </select>
          </label>

          <div className="bt-submit">
            <button className="bt-s" type="submit" disabled={submitting}>
              บันทึก
            </button>
            <button
              className="bt-cancel"
              type="button"
              onClick={() => {
                navigate(-1);
              }}
            >
              ยกเลิก
            </button>
          </div>
        </form>
      )}
    </Form>
  );
}
