import { useLoaderData } from "react-router-dom";

const postData = async (data) => {
  try {
    const response = await fetch("/your-post-url", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    });

    if (!response.ok) {
      throw new Error("Error posting data");
    }

    // ดำเนินการเพิ่มเติมหลังจากที่ส่งข้อมูลสำเร็จ
    console.log("Data posted successfully");
  } catch (error) {
    console.error(error);
  }
};

const TableDetailReserve = () => {
  const tableID = useLoaderData();

  const handleReservation = () => {
    const data = {
      customerName: "ทิว",
      table_id: tableID.table_id,
      reservationDate: "12-10-2550",
    };
    postData(data);
  };

  if (!tableID) {
    return <div>Loading...</div>;
  }

  return (
    <>
      <div key={tableID.table_id}>
        <div>
          <span>Zone: </span>
          {tableID.zone}
        </div>
        <div>
          <span>Table: </span>
          {tableID.table_number}
        </div>
        <div>
          <span>จองวันที่: </span>
          {tableID.reservationDate}
        </div>
        <button onClick={handleReservation}>จอง</button>
      </div>
    </>
  );
};

export default TableDetailReserve;
