import { useLoaderData, useLocation } from "react-router-dom";

export const tableLoader = async ({ params }) => {
  const { zone } = params;
  const url = zone ? `/table/zone/${zone}` : "/table";
  const res = await fetch(url);
  let tables = await res.json();
  if (!res.ok) {
    throw Error(tables.error);
  }
  return tables;
};

function status(props) {
  if (props === true) {
    console.log("p1 =" + props);
    return <div className="info">จองแล้ว</div>;
  } else {
    console.log("p1 =" + props);
    return <div className="danger">ยังไม่ได้จอง</div>;
  }
}

const TableListPage = () => {
  const tables = useLoaderData();
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const zone = params.get("zone");

  const updateZoneFilter = (newZone) => {
    const newPath = newZone ? `/reservation/table/zone/${newZone}` : "/table";
    window.history.pushState(null, "", newPath);
    // อัพเดต URL โดยไม่รีเฟรชหน้าเว็บ
  };

  return (
    <>
      <h1>รายการโต๊ะ</h1>
      <div>
        <div>
          <button onClick={() => updateZoneFilter(null)}>ทั้งหมด</button>{" "}
          <button onClick={() => updateZoneFilter("A")}>โซน A</button>{" "}
          <button onClick={() => updateZoneFilter("B")}>โซน B</button>{" "}
          <button onClick={() => updateZoneFilter("C")}>โซน C</button>
        </div>
        {params.map((table) => (
          <div key={table.table_id}>
            <h2>โต๊ะเลขที่: {table.table_number}</h2>
            <p>จำนวนที่นั่ง: {table.capacity}</p>
            <p>โซน: {table.zone}</p>
            <p>{status(table.available)}</p>
            <button>จอง</button>
          </div>
        ))}
      </div>
    </>
  );
};

export default TableListPage;
