import { Link, useLoaderData } from "react-router-dom";
import { useState } from "react";
import _ from "lodash";

export const tableLoader = async () => {
  const res = await fetch("/table");
  if (!res.ok) {
    throw new Error("Could not fetch the Tables");
  }
  return res.json();
};

function status(props) {
  if (props === true) {
    return <div className="info">Available</div>;
  } else {
    return <div className="danger">Booked</div>;
  }
}

const TableListPage = () => {
  const tables = useLoaderData();
  const [filteredTables, setFilteredTables] = useState(tables);

  const filterTablesByZone = (zone) => {
    const filteredTables = tables.filter((table) => table.zone === zone);
    return filteredTables;
  };

  const handleAddToreservation = (e) => {
    let reservation = [];
    if (localStorage.getItem("reservation")) {
      reservation = JSON.parse(localStorage.getItem("reservation"));
    }
    reservation.push({
      ...e,
      count: 1,
    });
    let unique = _.unionWith(reservation, _.isEqual);
    localStorage.setItem("reservation", JSON.stringify(unique));
  };

  // layout restaurent
  const [selectedTable, setSelectedTable] = useState(null);

  const handleTableClick = (tableId) => {
    setSelectedTable(tableId);
  };

  const tableLayout = [
    { id: 1, zone: "A", capacity: 4, reserved: true },
    { id: 2, zone: "A", capacity: 2, reserved: true },
    { id: 3, zone: "B", capacity: 4, reserved: false },
    { id: 4, zone: "B", capacity: 6, reserved: false },
    // Add more tables here...
  ];

  const zones = Array.from(new Set(tableLayout.map((table) => table.zone)));

  return (
    <>
      <div className="restaurant-layout">
        {zones.map((zone) => (
          <div key={zone} className="zone">
            <h2>Zone {zone}</h2>
            <div className="tables">
              {tableLayout
                .filter((table) => table.zone === zone)
                .map((table) => (
                  <div
                    key={table.id}
                    className={`table ${table.reserved ? "reserved" : ""} ${
                      selectedTable === table.id ? "selected" : ""
                    }`}
                    onClick={() => handleTableClick(table.id)}
                  >
                    {table.capacity}
                  </div>
                ))}
            </div>
          </div>
        ))}
      </div>
      <h1>Table List</h1>
      <div>
        <button onClick={() => setFilteredTables(tables)}>All</button>{" "}
        <button onClick={() => setFilteredTables(filterTablesByZone("A"))}>
          Zoness A
        </button>{" "}
        <button onClick={() => setFilteredTables(filterTablesByZone("B"))}>
          Zone B
        </button>{" "}
        <button onClick={() => setFilteredTables(filterTablesByZone("C"))}>
          Zone C
        </button>{" "}
        <button onClick={() => setFilteredTables(filterTablesByZone("D"))}>
          Zoness D
        </button>
      </div>

      <div>
        {filteredTables.map((table) => (
          <div key={table.table_id}>
            <div className="img-table">
              <img
                src={require(`../img/${table.table_number}.jpg`)}
                alt=""
                style={{ width: "320px", height: "300px", objectFit: "cover" }}
              />
            </div>
            <Link
              to={table.table_id}
              onClick={() => handleAddToreservation(table)}
            >
              <h2>Table {table.table_number}</h2>
            </Link>
            <p>Seats: {table.capacity}</p>
            <div>{status(table.available)}</div>
          </div>
        ))}
      </div>
    </>
  );
};

export default TableListPage;
