import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const ReservePage = () => {
  const reserve = JSON.parse(localStorage.getItem("reservation")) || [];
  const [name, setName] = useState("");
  const [date, setDate] = useState("");
  const navigate = useNavigate();

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleDateChange = (event) => {
    setDate(event.target.value);
  };

  const handleReserve = async (event) => {
    event.preventDefault();
    // create reservationData object
    const reservationData = {
      customerName: name,
      table_id: reserve[0].table_id,
      reservationDate: date,
      reservationTime: time,
    };
    try {
      const response = await fetch("/reserve", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(reservationData),
      });

      if (!response.ok) {
        throw new Error("Could not create reservation.");
      }

      // Clear localStorage
      localStorage.removeItem("reservation");
      // set success message for reservation
    } catch (error) {
      console.error(error);
    }
  };

  const handleCancel = () => {
    // Clear form inputs
    setName("");
    setDate("");
    // Clear localStorage
    localStorage.removeItem("reservation");
    // Navigate back to the previous page
    navigate(-1);
  };

  useEffect(() => {
    const handlePopstate = () => {
      // Clear localStorage
      localStorage.removeItem("reservation");
    };

    window.addEventListener("popstate", handlePopstate);

    return () => {
      window.removeEventListener("popstate", handlePopstate);
    };
  }, []);

  return (
    <>
      <form onSubmit={handleReserve}>
        <div className="container-reserve">
          <div className="row">
            <div className="name-reserve">
              <label htmlFor="name">ชื่อผู้จอง:</label>
              <input
                type="text"
                id="name"
                name="name"
                value={name}
                onChange={handleNameChange}
                required
              />
            </div>
            <div className="date-reserve">
              <label htmlFor="date">วันที่:</label>
              <input
                type="date"
                id="date"
                name="date"
                value={date}
                onChange={handleDateChange}
                required
              />
            </div>
          </div>

          <div className="row">
            {reserve.map((item, index) => (
              <div className="reserve-item" key={index}>
                <div className="reserve-item-title">
                  <div>
                    <span>Zone:</span>
                    {item.zone}
                  </div>
                  <div>
                    <span>โต๊ะ: </span>
                    {item.table_id}
                  </div>
                </div>
              </div>
            ))}
          </div>

          <div className="bt-reserve">
            <button type="submit" className="bt-reserve-submit">
              ยืนยันการจอง
            </button>
            <button
              type="button"
              className="bt-reserve-cancel"
              onClick={handleCancel}
            >
              ยกเลิก
            </button>
          </div>
        </div>
      </form>
    </>
  );
};

export default ReservePage;
