import { useLoaderData } from "react-router-dom";

export const tableDetailLoader = async ({ params }) => {
  const { id } = params;
  const res = await fetch("/table/" + id);

  let product = await res.json();
  if (!res.ok) {
    throw Error(product.error);
  }
  return product;
};

const TableDetailReseve = () => {
  const tableID = useLoaderData();

  if (!tableID) {
    return <div>Loading...</div>;
  }

  return (
    <>
      <div key={tableID.table_id}>
        <div>
          <span>Zone </span>
          {tableID.zone}
        </div>
        <div>
          <span>Table </span>
          {tableID.table_number}
        </div>
        <div>
          <span>จองวันที่</span>
        </div>
      </div>
    </>
  );
};

export default TableDetailReseve;
