import { useLoaderData, useLocation, Link } from "react-router-dom";

export const tableLoader = async ({ params }) => {
  const { zone } = params;
  const url = zone ? `/table/zone/${zone}` : "/table";
  const res = await fetch(url);
  let tables = await res.json();
  if (!res.ok) {
    throw Error(tables.error);
  }
  return tables;
};

function status(props) {
  if (props === true) {
    console.log("p1 =" + props);
    return <div className="info">จองแล้ว</div>;
  } else {
    console.log("p1 =" + props);
    return <div className="danger">ยังไม่ได้จอง</div>;
  }
}

const TableListPage = () => {
  const tables = useLoaderData();
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const zone = params.get("zone");

  return (
    <>
      <h1>รายการโต๊ะ</h1>
      <div className="table-list-container">
        {" "}
        {/* เพิ่มคลาส CSS เพื่อกำหนดความสูงและลักษณะการเลื่อน */}
        <div className="table-list">
          <div>
            <Link to="/table">ทั้งหมด</Link>{" "}
            <Link to="/table?zone=A">โซน A</Link>{" "}
            <Link to="/table?zone=B">โซน B</Link>{" "}
            <Link to="/table?zone=C">โซน C</Link>
          </div>
          {tables.map((table) => (
            <div key={table.table_id}>
              <h2>โต๊ะเลขที่: {table.table_number}</h2>
              <p>จำนวนที่นั่ง: {table.capacity}</p>
              <p>โซน: {table.zone}</p>
              <p>{status(table.available)}</p>
              <button>จอง</button>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default TableListPage;
