import { useEffect, useState } from "react";
import { Form, useNavigate, useParams } from "react-router-dom";

async function updateTable(table_id, updatedTable) {
  try {
    let response = await fetch(`/table/${table_id}`, {
      method: "PUT",
      body: JSON.stringify(updatedTable),
      headers: new Headers({
        "Content-Type": "application/json",
      }),
    });
    if (!response.ok) {
      throw new Error(`Could not update Table ${table_id}`);
    }
    return response.json();
  } catch (error) {
    console.error("Error:", error);
  }
}

export async function updateAction({ values, params }) {
  const { table_id } = params;
  const updatedTable = { table_id, ...values };
  await updateTable(table_id, updatedTable);
  return "/admin/";
}

export default function EditTable() {
  const { table_id } = useParams();
  const navigate = useNavigate();
  console.log(table_id);

  // Load initial table data from API
  const [table, setTable] = useState(null);
  useEffect(() => {
    async function fetchTable() {
      try {
        const response = await fetch(`/table/${table_id}`);
        if (!response.ok) {
          throw new Error(`Could not fetch Table ${table_id}`);
        }
        const data = await response.json();
        setTable(data);
      } catch (error) {
        console.error("Error:", error);
      }
    }
    fetchTable();
  }, [table_id]);

  // Render loading state while waiting for table data to load
  if (!table) {
    return <div>Loading...</div>;
  }

  return (
    <Form onSubmit={updateAction} initialValues={table}>
      <label>
        Table ID
        <input type="text" name="table_id" disabled />
      </label>
      <label>
        Table number
        <input type="text" name="table_number" />
      </label>
      <label>
        Capacity
        <input type="text" name="capacity" />
      </label>
      <label>
        Status
        <select name="available">
          <option value="true">Available</option>
          <option value="false">Booked</option>
        </select>
      </label>
      <label>
        Zone
        <input type="text" name="zone" />
      </label>
      <div className="bt-submit">
        <button className="bt-s" type="submit">
          บันทึก
        </button>
        <button
          className="bt-cancel"
          type="button"
          onClick={() => {
            navigate(-1);
          }}
        >
          ยกเลิก
        </button>
      </div>
    </Form>
  );
}
