import { useState, useCallback, useEffect, useRef } from "react";
import {
  useLocation,
  useNavigate,
  Link,
  useNavigation,
} from "react-router-dom";
import { useAuth } from "../utils/AuthProvider";
import Spinner from "../component/Spinner";
const LoginPage = () => {
  const auth = useAuth();
  const [loginLoading, setLoginLoading] = useState(false);

  const navigate = useNavigate();
  const navigation = useNavigation();
  const location = useLocation();

  let from = location.state?.from?.pathname || "/";
  const emailRef = useRef();
  const errRef = useRef();

  const [error, setError] = useState(null);

  useEffect(() => {
    emailRef.current.focus();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData(e.target);
    const email = data.get("email");
    const password = data.get("password");

    try {
      setLoginLoading(true);
      const response = await fetch("/auth", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password }),
      });
      if (response.ok) {
        const data = await response.json();
        const accessToken = data?.accessToken;
        const roles = data?.roles;
        const user = { email, roles, accessToken };
        await auth.signin(user, () => {
          return navigate(from, { replace: true });
        });
      } else if (response.status === 400) {
        setError("Missing Email or Password");
      } else if (response.status === 401) {
        setError("Unauthorized");
        console.log(">>> ", from);
      } else {
        setError("Email หรือ Password ไม่ถูกต้อง");
      }
    } catch (error) {
      setError("Error. Try again later (" + error + ")!");
    } finally {
      setLoginLoading(false);
    }
    errRef.current.focus();
  };

  const redirect = useCallback(() => {
    if (!loginLoading && auth?.user?.email) {
      if (from) {
        return navigate(from, { replace: true });
      } else {
        if (window.history.state && window.history.state.idx > 0) {
          navigate(-1); //, { replace: true });
        } else {
          navigate("/", { replace: true });
        }
      }
    }
  }, [auth.user.email, from, loginLoading, navigate]);

  useEffect(() => redirect(), [redirect]);

  return (
    <>
      {navigation.state === "loading" ? (
        <Spinner />
      ) : (
        <>
          <div className="login-container">
            <form onSubmit={handleSubmit} className="login-form">
              <h1 className="login-header">
                Login <br />
                TU Passadu
              </h1>
              <p ref={errRef} className={error ? "errmsg" : "hide"}>
                {error}
              </p>
              <div className="form-input">
                <label htmlFor="email" className="email-label">
                  Email
                </label>
                <input
                  type="text"
                  name="email"
                  id="email"
                  ref={emailRef}
                  placeholder="youremail@site.com"
                  autoComplete="off"
                  required
                />
              </div>
              <div className="form-input-p">
                <label htmlFor="password" className="password-label">
                  Password
                </label>
                <input
                  type="password"
                  name="password"
                  id="password"
                  placeholder="password"
                  autoComplete="off"
                  required
                />
              </div>
              <button type="submit" className="login-button">
                Login
              </button>
            </form>
          </div>
          <div className="login-signup">
            คุณยังไม่มี User ใช่หรือไม่ {"   "}
            <Link to="/register" className="signup-link">
              สมัคร
            </Link>{" "}
          </div>
        </>
      )}
    </>
  );
};

export default LoginPage;
