import React, { useState } from 'react';
import './Table.css';

export const loadReserve = async () => {
  const res = await fetch("/reserve");
  if (!res.ok) {
    throw Error("Could not fetch the reserve");
  }

  return res.json();
};
function status(props) {
  if (props === true) {
    return <div className="info">Available</div>;
  } else {
    return <div className="danger">Booked</div>;
  }
}


function Table() {
  const myrerve = useLoaderData();

  const MyreserveRows = myrerve.map((e, index) => (
    <tr key={index}>
      <td>{e.tableInfo.table_number}</td>
      <td>{e.tableInfo.zone}</td>
      <td>{e.customerName}</td>
      <td>{e.reservationDate}</td>
      <td>{e.reservationTime}</td>
      <td>{status(e.tableInfo.available)}</td>
    </tr>
  ));


  // Function to handle searching/filtering the table


  return (
    <div className="table-container">
      <div className="top-bar">
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search..."


          />
        </div>
        <button className="add-button" >Add</button>
      </div>
      <table className="table">
        <thead>
          <tr>
            <th>Table No.</th>
            <th>Zone</th>
            <th>Name</th>
            <th>Date Booking</th>
            <th>Time Booking</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {/* Render table rows dynamically */}
          {data.map((row, index) => (
            <tr key={index}>
              <td>{row.column1}</td>
              <td>{row.column2}</td>
              <td>{row.column3}</td>
              <td>{row.column4}</td>
              <td>{row.column5}</td>
              <td>{row.column6}</td>
              <td>
                <button onClick={() => handleEditRow(index)}>Edit</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Table;
